fn get_floor(instruction: &str) -> i32 {
    let mut floor = 0;
    let chars: Vec<char> = instruction.chars().collect();
    for step in chars {
        if step == '(' {
            floor += 1;
        } else {
            floor -= 1;
        }
    }

    floor
}

