    assert_eq!(get_floor("(())"), 0);
    assert_eq!(get_floor("()()"), 0);

    assert_eq!(get_floor("((("), 3);
    assert_eq!(get_floor("(()(()("), 3);
    assert_eq!(get_floor("))((((("), 3);

    assert_eq!(get_floor("())"), -1);
    assert_eq!(get_floor("))("), -1);

    assert_eq!(get_floor(")))"), -3);
    assert_eq!(get_floor(")())())"), -3);


