fn get_position(instruction: &str) -> i32 {
    let mut floor = 0;
    let mut position = 0;
    let v: Vec<char> = instruction.chars().collect();

    for step in v {
        if step == '(' {
            floor += 1;
        } else {
            floor -= 1;
        }

        position += 1;

        if floor == -1 {
            break;
        }
    }

    position
}

